import React, { Component } from 'react';
import { Card, Button,Image } from 'semantic-ui-react';
import factory from "../ethereum/factory";


class PropertyIndex extends Component {
    static async getInitialProps() {
        const properties = await factory.methods.getDeployedProperties().call();
        return { properties};
    }

    renderProperties(){
        const items = this.props.properties.map(address => {
          return {
              header: address,
              description: <a>View Property</a>,
            //  image: "https://react.semantic-ui.com/images/avatar/large/matthew.png",
            fluid: true
          };
        });
        return <Card.Group items={items} />;
    }

    render(){
        return <div>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.css" ></link>
                <h3>Properties</h3>
                {this.renderProperties()}
                <Button
                    content="Add Property"
                    icon="add circle"
                    primary
                />
                </div>
    }
}

export default PropertyIndex;
